import "./App.css";
import ScrollToTop from "./components/ScrollTop";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useLocation,
} from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import FooterComp from "./components/FooterComp";
import { Container } from "react-bootstrap";
import NavBarComp from "./components/NavBarComp";
import Home from "./pages/Home";
import Carrinho from "./pages/Carrinho";
import Controladores from "./pages/controladores";
import Estado from "./pages/EstadoSolido";
import Login from "./pages/login";
import ContaCliente from "./pages/ContaCliente";
import PainelAdmin from "./pages/PainelAdmin";
import ProdutosAdmin from "./components/ProdutosAdmin";
import ClientesAdmin from "./components/ClientesAdmin";
import AtualizarCatalogo from "./components/AtualizarCatalogo";
import Digital from "./pages/TemporizadorDigital";

const AppContent = () => {
  const location = useLocation();
  const isLoginPage =
    location.pathname === "/login" || location.pathname === "/login"; // Adicionando a rota /perfil

  return (
    <>
      {!isLoginPage && <NavBarComp />}
      <div className="app-content">
        <Container className={!isLoginPage ? "mt-5" : ""}>
          <ScrollToTop />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/Carrinho" element={<Carrinho />} />
            <Route path="/controladores" element={<Controladores />} />
            <Route path="/EstadoSolido" element={<Estado />} />
            <Route path="/login" element={<Login />} />
            <Route path="/TemporizadorDigital" element={<Digital />} />
            <Route path="/login" element={<login />} />{" "}
            <Route path="/cliente" element={<ContaCliente />} />
            <Route path="/admin" element={<PainelAdmin />} />
            {/* Exemplo de p√°gina de perfil */}
          </Routes>
        </Container>
      </div>
      {!isLoginPage && <FooterComp />}
    </>
  );
};

const App = () => {
  return (
    <Router>
      <AppContent />
    </Router>
  );
};

export default App;
